mapper.select:
select COUNT(gathering_id) as cnt 
from gathering_members gm
where gathering_state in (1, 2) and gathering_id = #{gatheringId}

mapper.delete:
delete from gathering_members
where gathering_id = #{gatheringId}
and members_id = #{membersId}

mapper.select:
select gathering_state
from gathering_members
where gathering_id = #{gatheringId} and members_id = #{membersId}
